
var t = require('..');

describe('tuple(key, val, ...)', function(){
  it('should default values to undefined', function(){
    var obj = t('foo');
    obj.should.eql({ foo: undefined });
  })

  it('should generate key/value pairs', function(){
    var obj = t('foo', 'bar');
    obj.should.eql({ foo: 'bar' });
  })

  it('should be variadic', function(){
    var obj = t('foo', 'bar', 'bar', 'baz');
    obj.should.eql({ foo: 'bar', bar: 'baz' });
  })
})